import React,{useState} from 'react'
import './login.css'
import {Link} from 'react-router-dom'
import classNames from 'classnames'
export default function(props) {

    const [formData,setFormData] = useState({
        email:"",
        password:""
    })
    const [activateButton,setActivateButton] = useState(false)
    const [error,setError] = useState(false)
    const [showPassword,setShowPassword] = useState(true)

    function handleChange (event) {
        
        const {name,value} = event.target
        setFormData(prevState => ({
            ...prevState,
            [name]:value
        }))
        if(handleValidity())
            setActivateButton(true)
        else
            setActivateButton(false)
    }

    function handleValidity() {
    
        const emailInput = document.querySelector('.email')
        const passwordInput = document.querySelector('.password')
        if(emailInput.checkValidity() && passwordInput.checkValidity())
            return 1
        return 0
    }
    function handleSubmit(event) {
    
        event.preventDefault()
        if(formData.email !== 'tudorbodea12@yahoo.com' || formData.password !== '12345678') {
            setError(true)
            console.log(error)
            return
        }
        else setError(false)
        props.toggleLogin()
        // setFormData({
        //     email:"",
        //     password:""
        // })
    }

    function toggleShowPassword () {
        setShowPassword(prevState => !prevState)
    }
    return (
        
        !props.login && <div className='login'>
            <div className='screen--black'
                onClick={props.toggleOff}
            >
            </div>
            <form className='sign--in' onSubmit={handleSubmit}>
                <button className='close' onClick={props.toggleOff}>âœ–</button>
                <h2 className='sign--title'>Sign in</h2>
                <div className='email--container'>
                    <span className='placeholder'>Email</span>
                    <input
                        onChange = {handleChange}
                        name = "email"
                        className='email'
                        type = "email"
                        pattern= "^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"
                        required
                        placeholder='Email'
                        value = {formData.email}
                    />
                </div>
                {/* <span className='login--error'>It should be a valid email adress!</span> */}
                <div className='password--container'>
                    <input 
                        onChange = {handleChange}
                        name = "password"
                        className = {classNames('password',{'empty':!formData.password.length})}
                        type = {showPassword ? 'password' : 'text'}
                        required
                        minLength={8}
                        value = {formData.password}
                    />
                    {formData.password && <div className='show--container'>
                    <button 
                        onClick = {toggleShowPassword} 
                        type = 'button' 
                        className={classNames('show',{'show--on':showPassword})}
                    >
                        {showPassword ? 'Show' : 'Hide'}
                    </button>
                    </div>}
                </div>
                {error && <span className='login--error'>Sorry, your password was incorrect. Please double-check your password.</span>}
                <button className={classNames('sign--submit',{"valid--value":activateButton})} type={activateButton ? 'submit' : 'button'}>Sign in</button>
                <footer className='sign--in--footer'>
                    <Link className='text-link change--password'>Forgot password?</Link>
                    <hr></hr>
                    <p><span>Don't have an account?</span><Link to = {'/createaccount'} className='text-link register' onClick={() => props.toggleLogin()}>Register</Link></p>
                </footer>
            </form>
        </div>
  )
}
