import React,{useState} from 'react'
import './login.css'
import {Link} from 'react-router-dom'
import classNames from 'classnames'
export default function(props) {

    const [formData,setFormData] = useState({
        email:"",
        password:""
    })
    const [activateButton,setActivateButton] = useState(false)
    const [error,setError] = useState(true)

    function handleChange (event) {
        const {name,value} = event.target
        setFormData(prevState => ({
            ...prevState,
            [name]:value
        }))
        if(formData.email.length !== 0 && formData.password.length >= 8)
            setActivateButton(true)
        else
            setActivateButton(false)
    }

    function handleSubmit(event) {
        if(activateButton === false) return

        console.log("da")
        event.preventDefault()
        //formData.email === 'tudorbodea12@yahoo.com' && formData.password === 'exemplu1' ? true : false
        ///setError(false)
        ///console.log(error)
       /// if(error === true) return

        props.toggleLogin()
        // setFormData({
        //     email:"",
        //     password:""
        // })
    }
    // console.log(error)
    return (
        
        !props.login && <div className='login'>
            <div className='screen--black'
                onClick={props.toggleOff}
            >
            </div>
            <form className='sign--in' onSubmit={handleSubmit}>
                <button className='close' onClick={props.toggleOff}>âœ–</button>
                <h2 className='sign--title'>Sign in</h2>
                <input
                    onChange = {handleChange}
                    name = "email"
                    className='email'
                    type = "email"
                    required
                    placeholder='Email'
                    value = {formData.email}
                />
                <span>It should be a valid email adress!</span>
                <input 
                    onChange = {handleChange}
                    name = "password"
                    className='password'
                    type = "password"
                    required
                    placeholder='Password'
                    minLength={8}
                    value = {formData.password}
                />
                <span>Sorry, your password was incorrect. Please double-check your password.</span>
                <button className={classNames('sign--submit',{"valid--value":activateButton})}>Sign in</button>
                <footer className='sign--in--footer'>
                    <Link className='text-link change--password'>Forgot password?</Link>
                    <hr></hr>
                    <p><span>Don't have an account?</span><Link className='text-link register'>Register</Link></p>
                </footer>
            </form>
        </div>
  )
}
