import React, { useState } from 'react'
import ReactImagePickerEditor, { ImagePickerConf } from 'react-image-picker-editor';
import 'react-image-picker-editor/dist/index.css'
import Discard from './Discard';


export default function CreatePost(props) {

  const [imageSrc,setImageSrc] = useState("")

  const [discard,setDiscard] = useState(false)

  const config: ImagePickerConf = {
    borderRadius : '8px',
    language : 'en',
    width : '500px',
    height: '500px',
    objectFit: 'contain',
    compressInitial: null,
    hideDownloadBtn: true,
    hideAddBtn: true,
    
  }

  const toggleDiscard = () => {
    setDiscard(prevDiscard => !prevDiscard)
  }
  const initialImage = ""
  console.log(imageSrc)
  return (
    <>
      
      <div className='create-post' tabIndex={0} onKeyDown = {(event) => event.key === 'Escape' ? () => setDiscard(true): ""}>
        <div className='upload-photo'>
          <button type = 'button' className='close close-white' onClick={() => setDiscard(true)}>âœ–</button>
          <h3>Create new post</h3>
          < ReactImagePickerEditor
            imageSrcProp={initialImage}
            config={config}
            imageChanged={(newDataUri: any) => { setImageSrc(newDataUri) }} 
          />
          {discard && <Discard discard = {discard} />}
          {imageSrc && <button className='select-photo'>Select photo</button> }
        </div>
        <div className='black-screen'></div>
      </div>
    </>
  )
}
