import React,{useState} from 'react'
import test from '../../images/test.jpg'
import { InputText } from 'primereact/inputtext';
import { Dialog } from 'primereact/dialog';

import './EditProfile.css'
import classNames from 'classnames'
import { useNavigate } from 'react-router-dom';
import Avatar from 'react-avatar-edit';

export default function EditProfile() {

    const [dialog,setDialog] = useState(false)
    const [imgCrop,setImgCrop] = useState(false)
    const [formData,setFormData] = useState({
        image:test,
        username:"Username",
        email:"tudorbodea12@yahoo.com",
        phoneNumber:"0720450578"
    })

    const [activateButton,setActivateButton] = useState(false)

    function handleChange (event) {

        const {name,value,files} = event.target
        console.log(value)
        setActivateButton(true)
        setFormData(prevState => ({
            ...prevState,
            [name]:value
        }))
    }
    const navigate = useNavigate()

    function handleSubmit (event) {
        event.preventDefault()
        navigate('/account')
    }

    const toggleDialog = () => {
        setDialog(true)
    }
    const onCrop = (view) => {
        setImgCrop(view)
    }
    const onClose = () => {
        setImgCrop(null)
    }
    return (
        <div className='edit-profile'>
            <div className='top-section'>
                <img src = {formData.image}/>
                <div className='right'>
                    <p>Username</p>
                    {/* <Avatar width={200} height = {100} onClose = {onCrop} onCrop = {onClose}/> */}
                    <h5 onClick={toggleDialog} className = 'change-picture'>Change profile picture</h5>
                    <Dialog
                        visible = {dialog}
                        header = {() => {
                            <h2 className='change-picture'>Change Profile Picture</h2>
                        }}
                        onHide = {() => setDialog(false)}
                    >
                        <div className = 'new-picture' style={{backgroundColor:'white'}}>
                            <Avatar width={300} height = {300}/>
                            <button>Save</button>
                        </div>
                    </Dialog>
                </div>
            </div>
            <form className='new-data' onSubmit={handleSubmit}>
                
                <div className='username-label'>
                    <label htmlFor='username'>Username</label>
                    <input 
                        type= 'text'
                        name='username'
                        id='username'
                        value = {formData.username}
                        onChange = {handleChange}
                        placeholder = 'Username'
                    />
                </div>
                <div className='email-label'>
                    <label htmlFor='email'>Email</label>
                    <input 
                        type = 'email'
                        name = 'email'
                        id = 'email'
                        value={formData.email}
                        onChange = {handleChange}
                        placeholder = 'Email'
                    />
                </div>
                <div className='phone-number-label'>
                    <label htmlFor='phoneNumber'>Phone Number</label>
                    <input 
                        type = 'phone'
                        name = 'phoneNumber'
                        id = 'phoneNumber'
                        value={formData.phoneNumber}
                        onChange = {handleChange}
                        placeholder = 'Phone Number'
                    />
                </div>

                <button type = {activateButton ? 'submit' : 'button'}className={classNames('sign--submit',{"valid--value":activateButton})}>Submit</button>
            </form>
        </div>
    )
}
