import React,{useState} from 'react'
import { Dialog,DialogTitle,DialogContent,DialogActions,DialogContentText} from '@mui/material'
import Avatar from 'react-avatar-edit'

export default function ChangeProfilePhoto() {

  const [dialog,setDialog] = useState(false)
  const [imgCrop,setImgCrop] = useState(false)
  const [storeImage,setStoreImage] = useState([])

  const toggleDialog = () => {
    setDialog(true)
  }
  
  
  const onCrop = (view) => {
    setImgCrop(view)
  }
  const onClose = () => {
    setImgCrop(null)
  }

  const saveImage = () => {
    setStoreImage([...storeImage,{imgCrop}])
    setDialog(false)
  }
  return (
    <>
      <h5 onClick={toggleDialog} className = 'change-picture'>Change profile picture</h5>
      <Dialog
        open = {dialog}
        onClose = {() => setDialog(false)}
        aria-labelledby='dialog-title'
        aria-describedby='dialog-description'
      >
          <DialogTitle  
            style = {{background: 'linear-gradient(to right, #314755 0%, #26a0df  51%, #314755  100%)'}} 
            id = 'dialog-title' className='dialog-title'> <h3>Change profile picture </h3>
          <i 
            className="fa-solid fa-x" 
            onClick={() => setDialog(false)}
          >
          </i></DialogTitle>
          <DialogContent style = {{background: 'linear-gradient(to right, #314755 0%, #26a0df  51%, #314755  100%)'}}>
            <DialogContentText className='dialog-content' id = 'dialog-description'>
              <Avatar 
                width={400} 
                height = {300}
                onClose = {onClose}
                onCrop = {onCrop}

              ></Avatar>
              <button className='save'>Save</button>
            </DialogContentText>
          </DialogContent>
      </Dialog>
    </>
  )
}
